var gulp=require("gulp"),htmlmin=require("gulp-htmlmin");gulp.task("html",function(){gulp.src("*.html").pipe(htmlmin({collapseWhitespace:!0,removeComments:!0})).pipe(gulp.dest("dist"))});var scss=require("gulp-sass"),cssnano=require("gulp-cssnano");gulp.task("scss",function(){gulp.src("*.scss").pipe(scss()).pipe(gulp.dest("dist")).pipe(cssnano()).pipe(gulp.dest("dist"))});var imagemin=require("gulp-imagemin"),cache=require("gulp-cache");gulp.task("images",function(){gulp.src("image/*.{png,jpg,gif}").pipe(cache(imagemin({progressive:!0,svgoPlugins:[{removeViewBox:!1}],interlaced:!0}))).pipe(gulp.dest("dist/images"))});var uglify=require("gulp-uglify");gulp.task("js",function(){gulp.src(["*.js"]).pipe(uglify()).pipe(gulp.dest("dist/js"))}),gulp.task("watch",["browser-sync","scss","js","images","html"],function(){gulp.watch("*.scss",["scss"]),gulp.watch("js/*.js",["js"]),gulp.watch("img/*.*",["images"]),gulp.watch("*.html",["html"])});var browserSync=require("browser-sync").create(),reload=browserSync.reload;gulp.task("browser-sync",function(){browserSync.init({server:{baseDir:"*/index.html"}})});